import styled from "styled-components";


export const Container = styled.section`
  
.hard-skills{
  margin-top: 1.6rem;
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  gap: 1.8rem;
}
.hability{
  display: flex;
  flex-direction: column;
  align-items: center;

  img{
    width: 5rem;
  }
}
  

  @media (max-width: 960px){
    .projects{
      grid-template-columns: 1fr 1fr;
      .Myproject{
        display:none
       }
    }
   
  }

  @media (max-width: 740px){
    .projects{
      grid-template-columns: 1fr;
      .Myproject{
       display:none
      }
    }
  
  }
`
