import styled from "styled-components";


export const Container = styled.section`
  margin-top: 15rem;
  
  .hability{
    display: flex;
    flex-direction: column;
    align-items: center;

    img{
      width: 1rem;
    }
  }
  

  @media (max-width: 960px){
    .projects{
      grid-template-columns: 1fr 1fr;
      .Myproject{
        display:none
       }
    }
   
  }

  @media (max-width: 740px){
    .projects{
      grid-template-columns: 1fr;
      .Myproject{
       display:none
      }
    }
  
  }
`
